// SPDX-License-Identifier: MIT
pragma solidity ^0.8.21;

import "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import "@openzeppelin/contracts/token/ERC20/extensions/IERC20Metadata.sol";

interface IERC4626 is IERC20, IERC20Metadata {

    // 事件

    // 存款事件    
    event Deposit(
        address indexed sender, 
        address indexed owner, 
        uint256 assets, 
        uint256 shares
    );

    // 取款事件
    event Withdraw(
        address indexed sender, 
        address indexed receiver,
        address indexed owner, 
        uint256 assets, 
        uint256 shares
    );

    
   /**
    * @dev 返回金库的基础资产代币地址（用于存款、取款）
    */
    function asset() external view returns(address assetTokenAddress);

   /**
    * @dev 存款函数：用户向金库存入assets单位的基础资产，然后合约铸造shares单位的金库额度给receiver地址
    * - 必须是否Deposit 事件
    * - 如果资产不能存入，必须revert，比如存款数额大于上限等
    */   
    function deposit(uint256 assets, address receiver) external returns(uint256 shares);

   /**
    * @dev 铸造函数：用户需要存入assets单位的基础资产，然后合约给receiver地址铸造shares数量的金库额度
    * - 必须是否Deposit事件
    * - 如果全部金库额度不能铸造，必须 revert, 比如铸造数额大于上线等
    */ 
    function mint(uint256 shares, address receiver) external  returns(uint256 assets);
    

    /**
     * @dev 提款函数：owner地址销毁shares单位的金库额度，然后合约将assets单位的基础资产发送给receiver地址
     * - 释放 withdraw事件
     * - 如果全部基础资产不能提取，将revert
     */
    function withdraw(uint256 assets, address receiver, address owner) external returns(uint256 shares);

    /**
     * @dev 赎回函数：owner地址销毁shares数量的金额额度，然后合约将asserts单位的基础代币资产发给receiver地址
     * - 释放Withdraw事件
     * - 如果金库额度不能全部销毁，则revert
     */
    function redeem(uint256 shares, address receiver, address owner) external returns(uint256 assets);


    /**
     * @dev 返回金库中管理的基础资产代币总额
     * - 要包含利息
     * - 要包含费用
     * - 不能revert
     */
    function totalAssets() external view returns(uint256 totalManagedAssets);
    
    /**
     * @dev 返回利用一定数额的金库额度可以换取的基础资产
     * - 不要包含费用
     * - 不包含滑点
     * - 不能revert
     */
    function convertToAssets(uint256 shares) external view returns(uint256 assets);

    /**
     * @dev 返回利用一定数额的基础资产可以换取的金额额度
     * - 不要包含费用
     * - 不要包含滑点
     * - 不能 revert
     */
    function convertToShares(uint256 assets) external view returns(uint256 shares);

    


}

